const FOLLOW = 'FOLLOW'
const UNFOLLOW = 'UNFOLLOW'
const SET_USERS = 'SET-USERS'

let inishialState = {
    users: [
        /*{id: 1, followed: false, fullName: 'Sasha', status: 'boss', location: {city: 'Minsk', country: 'Belarus'} },
        {id: 1, followed: false, fullName: 'Dima', status: 'I am boss too', location: {city: 'Moscow', country: 'Russia'} },
        {id: 1, followed: false, fullName: 'Ivan', status: 'I am boss too', location: {city: 'Kiev', country: 'Ukraine'} },
*/
    ],
}

export const usersReducer = (state = inishialState, action) => {

    switch (action.type) {
        case FOLLOW:
            return {
                ...state,
                users: state.users.map(u => {
                    if (u.id === action.userId) {
                        return {...u, followed: true}
                    }
                    return u;
                })
            }
        case UNFOLLOW:
            return {
                ...state,
                users: state.users.map(u => {
                    if (u.id === action.userId) {
                        return {...u, followed: false}
                    }
                    return u;
                })
            }
        case SET_USERS: {
            return {...state, users: [...state.users, ...action.users]}
        }
        default:
            return state
    }
}

export const followAC = (userId) => ({ type: FOLLOW, userId })
export const unfollowAC = (userId) => ({type: UNFOLLOW, userId})
export const setUsersAC = (users) => ({type: SET_USERS, users})
