import React from "react";
import styles from "./users.module.css";
import userPhoto from "../../assets/images/user-logo.png";
import {NavLink} from "react-router-dom";
import axios from "axios";

export let Users = (props) => {

    let pagesCount = Math.ceil(props.totalUserCount / props.pageSize);

    let pages = [];
    for (let i = 1; i <= pagesCount; i++) {
        pages.push(i);
    }

    return <div>
        <div>
            {pages.map(p => {
                return <span className = {props.currentPage === p && styles.selectedPage}
                             onClick = { (e) => {props.onPageChanged(p)}}>{p}</span>
            })}

        </div>
        {
            props.users.map( u => <div key={u.id}>
                    <span>
                        <div>
                            <NavLink to={'/profile/' + u.id}>
                            <img src = {u.photos.small != null ? u.photos.small : userPhoto}
                                 className = {styles.userPhoto}/>
                                 </NavLink>
                        </div>
                        <div>
                            {u.followed
                                ? <button onClick = {() => {props.unfollow(u.id) } }>Unfollow</button>

                                : <button onClick = {() => {

                                    axios.post(`https://social-network.samuraijs.com/api/1.0/users?page=${this.props.currentPage}&count = ${this.props.pageSize}`)
                                        .then(response => {
                                            this.props.toggleIsFetching(false);
                                            this.props.setUsers(response.data.items);
                                            this.props.setTotalUserslCount(response.data.totalCount);
                                        });

                                    props.follow(u.id) } }>Follow</button> }
                        </div>
                    </span>
                <span>
                        <div>{u.name}</div>
                        <div>{u.status}</div>
                    </span>
                <span>
                        <div>{"u.location.country"}</div>
                        <div>{"u.location.city"}</div>
                    </span>
            </div>)
        }
    </div>
}
